// SPDX-License-Identifier: MIT
pragma solidity 0.6.12;

// REMIX
// import "https://github.com/OpenZeppelin/openzeppelin-contracts/blob/solc-0.6/contracts/token/ERC721/ERC721.sol";
// import "https://github.com/OpenZeppelin/openzeppelin-contracts/blob/solc-0.6/contracts/token/ERC20/ERC20.sol";
// import "https://github.com/OpenZeppelin/openzeppelin-contracts/blob/solc-0.6/contracts/math/SafeMath.sol";
// import "https://github.com/OpenZeppelin/openzeppelin-contracts/blob/solc-0.6/contracts/utils/Address.sol";

// TRUFFLE
import "@openzeppelin/contracts-upgradeable/token/ERC721/ERC721Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/CountersUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/math/SafeMathUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/AddressUpgradeable.sol";
import "../01_nft/OATImplementation.sol";
import "../BaseRelayRecipient.sol";

import "../01_nft/OATImplementation.sol";

// AUCTION CONTRACT FOR OAT
contract OATAuctionImplementation is OwnableUpgradeable, BaseRelayRecipient{
    using SafeMathUpgradeable for uint256;
    using AddressUpgradeable for address;
    using CountersUpgradeable for CountersUpgradeable.Counter;
    CountersUpgradeable.Counter private _auctionIds;
    address private oatAddress;
    OATImplementation private oat;

    event AuctionCreated(
        uint256 _index,
        address _creator,
        address _token, 
        uint256 _endTime
    );

    event AuctionBid(
        uint256 _index,
        address _bidder, 
        uint256 amount
    );

    event AuctionCancelled(
        uint256 _index,
        address _Owner
    );

    event Claim(
        uint256 auctionIndex, 
        address auctionowner
    );
    event ClaimOAT(
        uint256 auctionIndex, 
        address buyer
    );

    event BuyNow(
        uint256 auctionIndex,
        address buyer,
        address owner, 
        uint256 amount
    );

    enum Status {pending, active, finished}

    struct Auction {
        uint256 tokenId;
        address creator;
        uint256 startTime;
        uint256 endTime;
        uint256 duration;
        uint256 currentBidAmount;
        uint256 currentBidAmountUSD;
        address payable currentBidOwner;
        uint256 maxSellingPrice;
        uint256 maxSellingPriceUSD;
        uint256 bidCount;
        bool amountStatus;
        bool claimed;
        bool buyNowStatus;
    }
    mapping (uint256 => Auction) private auctions;
    uint256 private endedAuction; 
    uint256 private canceldAuction;

    // Could also map creater => auctions

    /**
     * This function acts as the constructor
     *
     */
    function initialize(address _oatAddress, address _trustedForwarder) external initializer {
        __Ownable_init();
        require(_oatAddress.isContract());
        oatAddress = _oatAddress;
        oat = OATImplementation(_oatAddress);
        trustedForwarder = _trustedForwarder;
    }


    /**
     * Set Trusted Forwarder
     *
     * @param newTrustedForwarder - Address of Trusted Forwarder
     */
     function setTrustedForwarder(address newTrustedForwarder) external onlyAdmin {
        trustedForwarder = newTrustedForwarder;
    }




    /**
    * Create the Auction
    * 
    * @param _tokenId -- Id of token for auction
    * @param _startPrice -- start Price for the auction
    * @param _startPriceUSD -- start Price of auction in USD
    * @param _startTime -- Time to start the auction
    * @param _maxSellingPrice -- buy now price of the nft
    * @param _maxSellingPriceUSD -- max selling price in USD
    * @param _duration -- Duration of the Auction
    * @param _nftOwner -- owner of the token
    */
    function createAuction(
        uint256 _tokenId,
        uint256 _startPrice,
        uint256 _startPriceUSD,
        uint256 _startTime,
        uint256 _maxSellingPrice,
        uint256 _maxSellingPriceUSD,
        uint256 _duration,
        address _nftOwner
    ) public returns (uint256) {
        require(oat.ownerOf(_tokenId) == _msgSender()
        || oat.getApproved(_tokenId) == _msgSender(), 
        "Artefy: You must own or approved for the OAT");

        require(oat.ownerOf(_tokenId) == _nftOwner, "Artefy: Owner wrong address passed.");
        require(_msgSender() != address(0), "Artey: sender address is not correct");
        require(_nftOwner != address(0), "Artey: sender address is not correct");
        // require(oat.getApproved(_tokenId) == address(this), "Artefy: You need to approve this contract"); Bcoz ownership is with admin wallet
        if (_startTime == 0) {
             _startTime = block.timestamp;  // block.timestamp;
        }
        require(_startTime + _duration * 1 hours > block.timestamp, "Artefy: End time is not greater than Current block time.");
        uint256 _endTime = _startTime + _duration * 1 hours;
        Auction memory auction =
            Auction({
                creator: _nftOwner,
                tokenId: _tokenId,
                startTime: _startTime,
                endTime: _endTime,
                duration: _duration,
                currentBidAmount: _startPrice,
                currentBidAmountUSD: _startPriceUSD,
                maxSellingPrice: _maxSellingPrice,
                maxSellingPriceUSD: _maxSellingPriceUSD,
                currentBidOwner: address(0),
                bidCount: 0,
                amountStatus: false, 
                claimed: false, 
                buyNowStatus: false
            });
        _auctionIds.increment();
        uint256 newId = _auctionIds.current();    
        auctions[newId] = (auction);

        emit AuctionCreated(
            newId,
            auction.creator,
            oatAddress, 
            _endTime
        );

        return newId;
    }


    /**
    * Buy now with MATIC
    *
    * @param _auctionIndex -- Index of the auction to buy 
    * @param _tokenId -- token id of the nft for auction index
    * @param _nftOwner -- owner of the nft
    */
    function buyNow(
        uint256 _auctionIndex,
        uint256 _tokenId, 
        address _nftOwner 
    ) public payable {
        require(_msgSender() != address(0), "Artey: sender address is not correct");
        require(_nftOwner != address(0), "Artey: sender address is not correct");
        require(auctions[_auctionIndex].buyNowStatus == false, "Artefy: NFT is already sold");
        require(oat.ownerOf(_tokenId) == _nftOwner, "Artefy: Owner wrong address passed.");
        require(auctions[_auctionIndex].tokenId == _tokenId, "Artefy: This token Id and item Id does not match.");

        require(msg.value == auctions[_auctionIndex].maxSellingPrice, "Artefy: Amount transefered is not equal to buy now price");

        require(oat.getApproved(_tokenId) == address(this), "Artefy: Owner needs to approve this contract"); //Bcoz ownership is with admin wallet
        Auction memory auction = auctions[_auctionIndex];

        if (auction.amountStatus == true){
            // Refund previous bidder
            require (refundBid(auction), "Fund not transfered Succesfully");
        }    
        (bool sent,) = auction.creator.call{value: auction.maxSellingPrice}("");
        require(sent, "Artefy: Failed to send amount"); // Tranfering Matic value as well.
        oat.transferFrom(auction.creator, _msgSender(), auction.tokenId);

        auctions[_auctionIndex].buyNowStatus = true; 
        auctions[_auctionIndex].claimed = true;

        emit BuyNow(_auctionIndex, _msgSender(), auction.creator, msg.value);
    }

    /**
    * Buy now 
    *
    * @param _auctionIndex -- Index of the auction to buy 
    * @param _tokenId -- token id of the nft for auction index
    * @param _nftOwner -- owner of the nft
    * @param _nftBuyer -- Buyer of the NFT
    */
    function buyNowFiat(
        uint256 _auctionIndex,
        uint256 _tokenId, 
        address _nftOwner,
        address _nftBuyer 
    ) public onlyAdmin returns (bool) {
        require(_msgSender() != address(0), "Artey: sender address is not correct");
        require(_nftOwner != address(0), "Artey: owner address is not correct");
        require(_nftBuyer != address(0), "Artey: buyer address is not correct");

        require(auctions[_auctionIndex].buyNowStatus == false, "Artefy: NFT is already sold");
        require(oat.ownerOf(_tokenId) == _nftOwner, "Artefy: Owner wrong address passed.");
        require(auctions[_auctionIndex].tokenId == _tokenId, "Artefy: This token Id and item Id does not match.");

        require(oat.getApproved(_tokenId) == address(this), "Artefy: Owner needs to approve this contract"); //Bcoz ownership is with admin wallet
        Auction memory auction = auctions[_auctionIndex];

        if (auction.amountStatus == true){
            // Refund previous bidder
            require (refundBid(auction), "Fund not transfered Succesfully");
        }    
        oat.transferFrom(auction.creator, _nftBuyer, auction.tokenId);

        auctions[_auctionIndex].buyNowStatus = true; 
        auctions[_auctionIndex].claimed = true;

        emit BuyNow(_auctionIndex, _nftBuyer, auction.creator, auction.maxSellingPriceUSD);
    }



    /**
    * Cancel the Auction
    *
    * @param _auctionIndex  -- Index of the auction to cancel
    * @param _tokenId  -- token id of the nft for auction Index
    * @param _nftOwner -- owner of the token Id
    */
    function cancelAuction(
        uint256 _auctionIndex,
        uint256 _tokenId, 
        address _nftOwner
    ) public {
        require(_msgSender() != address(0), "Artey: sender address is not correct");
        require(_nftOwner != address(0), "Artey: sender address is not correct");
        require(auctions[_auctionIndex].buyNowStatus == false, "Artefy: NFT is already sold");

        require(oat.ownerOf(_tokenId) == _msgSender() 
        || oat.getApproved(_tokenId) == _msgSender(), 
        "Artefy: You must own or approved for the OAT");

        require(oat.ownerOf(_tokenId) == _nftOwner, "Artefy: Owner wrong address passed.");

        require(auctions[_auctionIndex].tokenId == _tokenId, "Artefy: This token Id and item Id does not match.");
        delete auctions[_auctionIndex]; 
        emit AuctionCancelled(_auctionIndex, _nftOwner);

    }

    /**
    * To update auction data - Only owner can update it
    * 
    * @param _auctionIndex -- Index of the AUCTION
    * @param _tokenId -- Id of token
    * @param _buyNowPrice -- update buy now price 
    * @param _endTime  -- Time to end the Auction
    */
    function updateAuction(
        uint256 _auctionIndex, 
        uint256 _tokenId, 
        uint256 _buyNowPrice, 
        uint256 _endTime 
    ) public onlyAdmin {
        // require(condition);
        require(auctions[_auctionIndex].tokenId == _tokenId, "Artefy: This token Id and item Id does not match.");
        require(auctions[_auctionIndex].buyNowStatus == false, "Artefy: NFT is already sold");

        auctions[_auctionIndex].maxSellingPrice = _buyNowPrice;
        if (_endTime != 0){
            auctions[_auctionIndex].endTime = _endTime;
        }
    }

    /**
    * Bid for the Auction
    * 
    * @param auctionIndex -- ID of the auction
    * @param bidder -- Owner of the bid
    * @param amount -- Amount of Bid 
    * @param amountUSD -- Amount of bid in USD
    */
    function bidfiat(
        uint256 auctionIndex,
        address payable bidder, 
        uint256 amount, 
        uint256 amountUSD
    ) public onlyAdmin returns (bool) {
        Auction storage auction = auctions[auctionIndex];
        require(auction.creator != address(0), "Artefy: Bid creator address is not correct.");
        require(auctions[auctionIndex].buyNowStatus == false, "Artefy: NFT is already sold");
        require(isActive(auctionIndex), "Artefy: Bidding is not strated yet.");
        require (amountUSD > auction.currentBidAmountUSD, "Artefy: bid amount sent is not more than the previous bid." ); // Need Conversion, how much difference

        if (auction.amountStatus == true){
            // Refund Previous MATIC received
            require (refundBid(auction), "Fund not transfered Succesfully");
        }

        auction.currentBidAmount = amount;
        auction.currentBidAmountUSD = amountUSD;
        auction.currentBidOwner = bidder;
        auction.bidCount = auction.bidCount.add(1);
        auction.amountStatus = false;

        emit AuctionBid(auctionIndex, bidder, amount);
        return true;
    }

    /**
    * return the amount back to previous owner
    * 
    * @param -- auction object 
    */
    function refundBid(
        Auction memory auction
    ) internal returns (bool){
        uint256 transferAmount = auction.currentBidAmount; 
        (bool sent,) = auction.currentBidOwner.call{value: transferAmount}("");
        require(sent, "Artefy: Failed to send amount"); // Tranfering Matic value as well.
        return true;
    }


    /**
    * Bid for the Auction
    * 
    * @param auctionIndex -- ID of the auction
    * @param amount -- Bid amount 
    * @param amountUSD -- Bid Amount in USD
    */
    function bid(
        uint256 auctionIndex, 
        uint256 amount, 
        uint256 amountUSD
    ) public payable returns (bool) {
        require(auctions[auctionIndex].buyNowStatus == false, "Artefy: NFT is already sold");
        require (amount == msg.value , "Artefy: Amount sent is not enough to make a bid.");
        Auction storage auction = auctions[auctionIndex];
        require(auction.creator != address(0), "Artefy: Bid creator address is not correct.");
        require(isActive(auctionIndex), "Artefy: Bidding is not strated yet.");
        // Just checking current bid is greater, 
        require (amountUSD > auction.currentBidAmountUSD, "Artefy: bid amount sent is not more than the previous bid." ); // Need Conversion, how much difference

        if (auction.amountStatus == true) {
            // Transfer the MATIC back to user
            refundBid(auction);
        }

        // register new bidder
        auction.currentBidAmount = amount;
        auction.currentBidAmountUSD = amountUSD;
        auction.currentBidOwner = _msgSender();
        auction.bidCount = auction.bidCount.add(1);
        auction.amountStatus = true;

        emit AuctionBid(auctionIndex, _msgSender(), amount);
        return true;

    }


    /**
    *
    * GET TOTAL NUMBER OF AUCTIONS
    */ 
    function getTotalAuctions(
    ) public view returns (uint256) {
        return (_auctionIds.current() - (endedAuction + canceldAuction));
    }

    /**
    * CHECK IF AUCTION IS ACTIVE
    * 
    * @param index -- Auction index
    */ 
    function isActive(
        uint256 index
    ) public view returns (bool) {
        return getStatus(index) == Status.active;
    }

    /**
    * CHECK IF AUCTION IS FINISHED
    *
    * @param index - index of the Auction
    */
    function isFinished(
        uint256 index
    ) public view returns (bool) {
        return getStatus(index) == Status.finished;
    }

    /**
    * GET STATUS OF AUCTION
    *
    * @param index - index of the Auction
    */
    function getStatus(
        uint256 index
    ) public view returns (Status) {
        Auction storage auction = auctions[index];
        if (now < auction.startTime) {
            return Status.pending;
        } else if (now < auction.endTime) {
            return Status.active;
        } else {
            return Status.finished;
        }
    }

    /** 
    * GET OWNER OF CURRENT BID
    *
    * @param auctionIndex - index of the Auction
    */
    function getCurrentBidOwner(
        uint256 auctionIndex
    ) public view returns (address)
    {
        return auctions[auctionIndex].currentBidOwner;
    }

    /**
    * GET CURRENT BID AMOUNT
    *
    * @param auctionIndex - index of the Auction
    */ 
    function getCurrentBidAmount(
        uint256 auctionIndex
    ) public view returns (uint256)
    {
        return auctions[auctionIndex].currentBidAmount;
    }

    /**
    * GET AUCTION BID COUNT
    *
    * @param auctionIndex - index of the Auction
    */ 
    function getBidCount(
        uint256 auctionIndex
    ) public view returns (uint256) {
        return auctions[auctionIndex].bidCount;
    }

    /**
    * GET AUCTION WINNER
    *
    * @param auctionIndex - index of the Auction
    */ 
    function getWinner(
        uint256 auctionIndex
    ) public view returns (address) {
        require(isFinished(auctionIndex));
        return auctions[auctionIndex].currentBidOwner;
    }

    /**
    * GET AUCTION CREATOR
    *
    * @param auctionIndex - index of the Auction
    */ 
    function auctionCreator(
        uint256 auctionIndex
    ) public view returns (address){
        return auctions[auctionIndex].creator;
    }


    /**
    * AUCTION CREATOR - CLAIM TOKENS
    *
    * @param auctionIndex -- index of the auction
    */
    function claimAuctionAmount(
        uint256 auctionIndex
    ) public {
        require(auctions[auctionIndex].buyNowStatus == false, "Artefy: NFT is already sold");
        require(isFinished(auctionIndex), "Artefy: Auction is not finished yet.");
        Auction storage auction = auctions[auctionIndex];
        require(auction.claimed == false, "Artefy: Auction amount is already claimed.");
        require(oat.ownerOf(auction.tokenId) == address(this), "Artefy: Owner need to tranfer token to this contract"); //Bcoz ownership is with admin wallet

        require(auction.creator == _msgSender(), "Artefy: Only creator can claim Auction Amount.");   
        // need to transfer MATIC here to auction creator.
        if (auction.amountStatus == true){
            (bool sent,) = auction.creator.call{value: auction.currentBidAmount}("");
            require(sent, "Artefy: Failed to send amount"); // Tranfering Matic value as well.
        }    
        auction.claimed = true;
        endedAuction.add(1);
        emit Claim(auctionIndex, auction.creator);
    }


    /**
    * CURRENT BID OWNER - CLAIM OAT
    * 
    * @param auctionIndex -- Claim auction Token
    */
    function claimOat(
        uint256 auctionIndex
    ) public {
        require(auctions[auctionIndex].buyNowStatus == false, "Artefy: NFT is already sold");
        require(isFinished(auctionIndex), "Artefy: Auction is not finished yet.");
        Auction storage auction = auctions[auctionIndex];
        require(oat.ownerOf(auction.tokenId) == address(this), "Artefy: Owner must transfer the token to this contract"); //Bcoz ownership is with admin wallet
        address winner = getWinner(auctionIndex);
        require(winner == _msgSender(), "Artefy: Only Winner can claim token.");

        oat.transferFrom(address(this), winner, auction.tokenId);

        emit ClaimOAT(auctionIndex, winner);
    }


    /**
     * returns the message sender
     */
     function _msgSender()
     internal
     view
     override(ContextUpgradeable, BaseRelayRecipient)
     returns (address payable)
    {
        return BaseRelayRecipient._msgSender();
    }


     /**
     * Throws if called by any account other than the Admin.
     */
     modifier onlyAdmin() {
        require(
            oat.isAdmin(_msgSender()) || _msgSender() == owner(),
            "Caller does not have Admin Access"
        );
        _;
    }


}

